import React, { useState } from 'react';
import styled from 'styled-components';
import QuickSortVisualizer from './components/QuickSortVisualizer';

const AppContainer = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  padding: 20px;
`;

const Header = styled.header`
  background-color: #2c3e50;
  color: white;
  padding: 20px;
  border-radius: 8px;
  margin-bottom: 20px;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
`;

const Title = styled.h1`
  margin: 0;
  font-size: 2rem;
`;

const Subtitle = styled.p`
  margin: 10px 0 0;
  font-size: 1rem;
  opacity: 0.8;
`;

const AlgorithmSelector = styled.div`
  margin-bottom: 20px;
`;

const Select = styled.select`
  padding: 10px;
  font-size: 1rem;
  border-radius: 4px;
  border: 1px solid #ddd;
  background-color: white;
  width: 100%;
  max-width: 300px;
`;

const App: React.FC = () => {
  const [algorithm, setAlgorithm] = useState<string>('quicksort');

  const handleAlgorithmChange = (e: React.ChangeEvent<HTMLSelectElement>) => {
    setAlgorithm(e.target.value);
  };

  return (
    <AppContainer>
      <Header>
        <Title>DSA Visualization</Title>
        <Subtitle>Visualize Data Structures and Algorithms in action</Subtitle>
      </Header>

      <AlgorithmSelector>
        <Select value={algorithm} onChange={handleAlgorithmChange}>
          <option value="quicksort">Quick Sort</option>
          {/* Add more algorithms here in the future */}
        </Select>
      </AlgorithmSelector>

      {algorithm === 'quicksort' && <QuickSortVisualizer />}
      {/* Add more algorithm visualizers here in the future */}
    </AppContainer>
  );
};

export default App;
