import React from 'react';
import styled from 'styled-components';

interface QuickSortCodeProps {
  currentLineNumber: number;
}

const CodeContainer = styled.div`
  background-color: #282c34;
  border-radius: 8px;
  padding: 20px;
  margin-bottom: 20px;
  overflow-x: auto;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
`;

const CodeBlock = styled.pre`
  margin: 0;
  font-family: 'Consolas', 'Monaco', 'Andale Mono', 'Ubuntu Mono', monospace;
  font-size: 14px;
  line-height: 1.5;
  color: #abb2bf;
`;

const CodeLine = styled.div<{ isActive: boolean }>`
  padding: 2px 0;
  background-color: ${props => props.isActive ? 'rgba(97, 175, 239, 0.2)' : 'transparent'};
  border-left: ${props => props.isActive ? '3px solid #61afef' : '3px solid transparent'};
  padding-left: ${props => props.isActive ? '7px' : '10px'};
  transition: all 0.3s ease;
`;

const LineNumber = styled.span`
  display: inline-block;
  width: 30px;
  color: #636d83;
  user-select: none;
`;

const Keyword = styled.span`
  color: #c678dd;
`;

const Function = styled.span`
  color: #61afef;
`;

const Comment = styled.span`
  color: #98c379;
`;

const String = styled.span`
  color: #e5c07b;
`;

const Operator = styled.span`
  color: #56b6c2;
`;

const QuickSortCode: React.FC<QuickSortCodeProps> = ({ currentLineNumber }) => {
  // Define the code lines with syntax highlighting
  const codeLines = [
    <><LineNumber>1</LineNumber><Keyword>function</Keyword> <Function>quickSort</Function>(<Keyword>array</Keyword>) {'{'}</>,
    <><LineNumber>2</LineNumber>  <Keyword>if</Keyword> (array.length &lt;= <String>1</String>) <Keyword>return</Keyword> array;</>,
    <><LineNumber>3</LineNumber>  <Keyword>return</Keyword> <Function>quickSortHelper</Function>(array, <String>0</String>, array.length - <String>1</String>);</>,
    <><LineNumber>4</LineNumber>{'}'}</>,
    <><LineNumber>5</LineNumber></>,
    <><LineNumber>6</LineNumber><Keyword>function</Keyword> <Function>quickSortHelper</Function>(<Keyword>array</Keyword>, <Keyword>low</Keyword>, <Keyword>high</Keyword>) {'{'}</>,
    <><LineNumber>7</LineNumber>  <Keyword>if</Keyword> (low &lt; high) {'{'}</>,
    <><LineNumber>8</LineNumber>    <Keyword>const</Keyword> pivotIndex = <Function>partition</Function>(array, low, high);</>,
    <><LineNumber>9</LineNumber>    <Function>quickSortHelper</Function>(array, low, pivotIndex - <String>1</String>); <Comment>// Sort left part</Comment></>,
    <><LineNumber>10</LineNumber>    <Function>quickSortHelper</Function>(array, pivotIndex + <String>1</String>, high); <Comment>// Sort right part</Comment></>,
    <><LineNumber>11</LineNumber>  {'}'}</>,
    <><LineNumber>12</LineNumber>  <Keyword>return</Keyword> array;</>,
    <><LineNumber>13</LineNumber>{'}'}</>,
    <><LineNumber>14</LineNumber></>,
    <><LineNumber>15</LineNumber><Keyword>function</Keyword> <Function>partition</Function>(<Keyword>array</Keyword>, <Keyword>low</Keyword>, <Keyword>high</Keyword>) {'{'}</>,
    <><LineNumber>16</LineNumber>  <Keyword>const</Keyword> pivot = array[high]; <Comment>// Choose the rightmost element as pivot</Comment></>,
    <><LineNumber>17</LineNumber>  <Keyword>let</Keyword> i = low - <String>1</String>; <Comment>// Index of smaller element</Comment></>,
    <><LineNumber>18</LineNumber></>,
    <><LineNumber>19</LineNumber>  <Keyword>for</Keyword> (<Keyword>let</Keyword> j = low; j &lt; high; j++) {'{'}</>,
    <><LineNumber>20</LineNumber>    <Comment>// If current element is smaller than the pivot</Comment></>,
    <><LineNumber>21</LineNumber>    <Keyword>if</Keyword> (array[j] &lt; pivot) {'{'}</>,
    <><LineNumber>22</LineNumber>      i++; <Comment>// Increment index of smaller element</Comment></>,
    <><LineNumber>23</LineNumber>      [array[i], array[j]] = [array[j], array[i]]; <Comment>// Swap elements</Comment></>,
    <><LineNumber>24</LineNumber>    {'}'}</>,
    <><LineNumber>25</LineNumber>  {'}'}</>,
    <><LineNumber>26</LineNumber></>,
    <><LineNumber>27</LineNumber>  [array[i + <String>1</String>], array[high]] = [array[high], array[i + <String>1</String>]]; <Comment>// Swap pivot to its final position</Comment></>,
    <><LineNumber>28</LineNumber>  <Keyword>return</Keyword> i + <String>1</String>; <Comment>// Return the pivot index</Comment></>,
    <><LineNumber>29</LineNumber>{'}'}</>,
  ];

  return (
    <CodeContainer>
      <CodeBlock>
        {codeLines.map((line, index) => (
          <CodeLine key={index} isActive={index + 1 === currentLineNumber}>
            {line}
          </CodeLine>
        ))}
      </CodeBlock>
    </CodeContainer>
  );
};

export default QuickSortCode;
